+++ 
@@ -0,0 +1,129 @@
+@import url('https://fonts.googleapis.com/css2?family=JetBrains+Mono:wght@400;500;700&display=swap');
+* {
+    margin: 0;
+    padding: 0;
+    box-sizing: border-box;
+}
+
+:root {
+    --very-dark-grey:#18171f; 
+    --dark-grey: #24232c; 
+    --grey:#817D92; 
+    --almost-white:#e6e5ea; 
+    --neon-green:#a4ffaf; 
+}
+
+
+/* Main Styles */
+
+body {
+   background-color: var(--very-dark-grey); 
+   font-family: 'JetBrains Mono', monospace;
+   font-weight: 700;
+}
+
+.container {
+    margin: 10rem auto;
+    text-align: center;
+    max-width: 1100px;
+}
+
+/* Header */
+header {
+    color: var(--grey);
+    font-size: 1.5rem;
+    margin-bottom: 1.4rem;
+}
+
+/* Password Field */
+.password-field {
+    background-color: var(--dark-grey);
+    color: var(--almost-white);
+    display: flex;
+    justify-content: space-between;
+    align-items: center;
+    width: 33.75rem;
+    align-content: center;
+    margin: 0 auto;
+    padding: 0.5rem 1.5rem;
+    margin-bottom: 3rem;
+}
+
+
+/* Form Control */
+.form-control {
+    background-color: var(--dark-grey);
+    color: var(--almost-white);
+    padding: 20px;
+}
+
+.length-label {
+    color: var(--neon-green);
+    font-size: 2rem;
+}
+
+.character-field {
+    display: flex;
+    justify-content: space-around;
+}
+
+input[type="range"] {
+    /* removing default appearance */
+    -webkit-appearance: none;
+    appearance: none;
+    /* creating a custom design */
+    width: 476px;
+    cursor: pointer;
+    outline: none;
+  }
+
+
+
+
+  /* Track */
+  input[type="range"] {
+    /*  styling the track  */
+    height: 8px;
+    background: var(--very-dark-grey);
+    overflow: hidden;
+  }
+
+
+  /* Track: webkit browsers */
+input[type="range"]::-webkit-slider-runnable-track {
+    height: 8px;
+    background: var(--very-dark-grey);
+  }
+  
+  /* Track: Mozilla Firefox */
+  input[type="range"]::-moz-range-track {
+    height: 8px;
+    background: var(--very-dark-grey);
+  }
+
+
+  /* Thumb */
+
+  /* Thumb: Google Chrome */
+  input[type="range"]::-webkit-slider-thumb {
+    /* removing default appearance */
+    -webkit-appearance: none;
+    appearance: none;
+    /* creating a custom design */
+    height: 28px;
+    width:28px;
+    background-color: var(--almost-white);
+    border-radius: 50%;
+    box-shadow: -407px 0 0 400px var(--neon-green);
+  }
+
+/* Thumb: Firefox */
+  input[type="range"]::-moz-range-thumb {
+    height: 28px;
+    width: 28px;
+    background-color:var(--almost-white);
+    border-radius: 50%;
+    border: none;
+    box-shadow: -407px 0 0 400px var(--neon-green);
+  }
+

