+@import url('https://fonts.googleapis.com/css2?family=JetBrains+Mono:wght@400;500;700&display=swap');
+* {
+    margin: 0;
+    padding: 0;
+    box-sizing: border-box;
+}
+
+:root {
+    --very-dark-grey:#18171f; 
+    --dark-grey: #24232c; 
+    --grey:#817D92; 
+    --almost-white:#e6e5ea; 
+    --neon-green:#a4ffaf; 
+}
+
+
+/* Main Styles */
+
+body {
+   background-color: var(--very-dark-grey); 
+   font-family: 'JetBrains Mono', monospace;
+   font-weight: 700;
+}
+
+.container {
+    margin: 10rem auto;
+    text-align: center;
+}
+
+/* Header */
+header {
+    color: var(--grey);
+    font-size: 1.5rem;
+    margin-bottom: 1.4rem;
+}
+
+/* Password Field */
+.password-field {
+    background-color: var(--dark-grey);
+    color: var(--almost-white);
+    display: flex;
+    justify-content: space-between;
+    align-items: center;
+    width: 33.75rem;
+    align-content: center;
+    margin: 0 auto;
+    padding: 0.5rem 1.5rem;
+    margin-bottom: 1rem;
+}
+
+#password-text {
+  background-color: var(--dark-grey);
+  color: var(--almost-white);
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  width: 33.75rem;
+  padding: 0.5rem 1.5rem;
+}
+
+
+/* Form Control */
+.form-control {
+    background-color: var(--dark-grey);
+    color: var(--almost-white);
+    padding: 20px;
+    max-width: 540px;
+    margin: auto;
+}
+
+.length-label {
+    color: var(--neon-green);
+    font-size: 2rem;
+}
+
+.character-field {
+    display: flex;
+    justify-content: space-between;
+    align-items: center;
+}
+
+
+  /* Track */
+  input[type="range"] {
+    /*  styling the track  */
+    appearance: none;
+    -webkit-appearance: none;
+    background: var(--very-dark-grey);
+    width: 476px;
+    cursor: pointer;
+    outline: none;
+    height: 8px;
+    background: var(--very-dark-grey);
+   
+  }
+
+  /* Thumb */
+  /* Thumb: Google Chrome */
+  input[type="range"]::-webkit-slider-thumb {
+    /* removing default appearance */
+    -webkit-appearance: none;
+    appearance: none;
+    /* creating a custom design */
+ 
+    background-color: var(--almost-white);
+    border-radius: 50%;
+    height: 28px;
+    width:28px;
+    transition: .2s ease-in-out;
+  }
+
+/* Thumb: Firefox */
+  input[type="range"]::-moz-range-thumb {
+    height: 28px;
+    width: 28px;
+    background-color:var(--almost-white);
+    border-radius: 50%;
+    border: none;
+  }
+
+
+  /* Checkbox Main Styling */
+  .checkboxes {
+    display: flex;
+    flex-direction: column;
+    justify-content: flex-start;
+    gap: 1.5rem;
+    margin-top: 1.5rem;
+  }
+
+   /* Checkboxes tick styling */
+   input[type="checkbox"] {
+    visibility: hidden;
+   }
+
+   input[type="checkbox"] + label {
+        position: relative;
+        padding-left: 30px;
+        cursor: pointer;
+   }
+
+   input[type="checkbox"] + label::before {
+    content: "";
+    position: absolute;
+    left: 0;
+    top: 0;
+    width: 20px;
+    height: 20px;
+    border: 1px solid var(--almost-white);
+   }
+
+   input[type="checkbox"]:checked + label::before {
+    border: none;
+   }
+
+   input[type="checkbox"]:hover + label::before{
+    border: 1px solid var(--neon-green);
+   }
+
+   input[type="checkbox"]:checked + label::after {
+    content: "";
+    position: absolute;
+    left: 1px;
+    top: 0;
+    width: 20px;
+    height: 21px;
+    background:url(assets/images/icon-check.svg) no-repeat center; 
+    background-color: var(--neon-green);
+   }
+
+
+  .checkbox-group {
+    display: flex;
+    align-items: center;
+  }
+
+
+/* Password Strength */
+
+
+

